package Loops_practice;

public class Practice1{

		public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int row=1; row <= 5 ; row =row+1) {

		for(int column =1; column <= row; column = column +1)

		System.out.print("*");

		System.out.println();

		}

		}
}
