package Loops_practice;

public class Practice4 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=1;
		for(int row=1; row <= 4 ; row++) {
		for(int column=1; column <= row; column++) {
		System.out.print(n++);
		}
		System.out.println();

		}

		}
}
